sql.where:
1=1

	and id = #{id}


	and fromId = #{fromId}


	and title = #{title}


	and content = #{content}


	and contentTag = #{contentTag}


	and ifRespone = #{ifRespone}


	and ifShow = #{ifShow}


	and sendTime = #{sendTime}


	and bak1 = #{bak1}


	and bak2 = #{bak2}


	and bak3 = #{bak3}


	and fromId in (SELECT id FROM `user` WHERE nickName LIKE '%${search}%')
	or title LIKE '%${search}%'



	and bak1 in (SELECT id FROM `sys_dict` WHERE pid = #{gradeId})


	and bak1 = #{subjectId}


	and fromId in (SELECT id FROM `user` u WHERE u.schoolId IN (
			SELECT id FROM sys_school s WHERE s.cityId IN(SELECT id FROM sys_dict d WHERE d.`value` LIKE '%${area}%')
		))

mapper.update:
update web_question
set 
ifShow = #{ifShow}
where id in 

	#{id}

mapper.delete:
delete from web_question
where id in
 
	#{id}

mapper.select:
select , (SELECT nickName FROM `user` u WHERE u.id=t.fromId) as nickName, (SELECT filePath FROM `user` u WHERE u.id=t.fromId) as filepath, (SELECT value FROM `sys_dict` d where d.id=t.bak1) as subject, (SELECT value FROM `sys_dict` d where d.id = (SELECT pid FROM `sys_dict` i where i.id=t.bak1)) as grade from web_question t where t.ifRespone=0 and t.ifShow=1
and bak1 in (SELECT id FROM `sys_dict` WHERE pid = #{gradeId})
and bak1 = #{subjectId}
